/* eslint-disable jsx-a11y/img-redundant-alt */
import { useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import useFetch from "./useFetch";

const BlogDetails = () => {
  const { id } = useParams(); // ID du blog spécifique
  const { data: blog, isPending, error } = useFetch("http://localhost:8000/blogs/" + id);
  const [categoryFilter, setCategoryFilter] = useState(""); // État pour le filtre de catégorie
  const history = useHistory();

  const handleDelete = () => {
    fetch("http://localhost:8000/blogs/" + blog.id, {
      method: "DELETE",
    }).then(() => {
      history.push("/Home");
    });
  };

  // Fonction pour filtrer les blogs par catégorie
  const filterByCategory = (category) => {
    setCategoryFilter(category);
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 px-4">
      {isPending && <div className="text-gray-500">Loading...</div>}
      {error && <div className="text-red-500">{error}</div>}
      {blog && (
        <article className="mb-10 border-b border-gray-100 pb-6">
          <h2 className="text-2xl font-semibold text-pink-600 mb-4">{blog.title}</h2>

          {/* Affichage de la catégorie */}
          <p className="text-sm text-gray-500 mb-4">Category: {blog.category}</p>

          {/* Filtrage des blogs par catégorie */}
          <div className="mb-6">
            <button
              onClick={() => filterByCategory("Technology")}
              className="bg-blue-600 text-white px-4 py-2 rounded-md"
            >
              Technology
            </button>
            <button
              onClick={() => filterByCategory("Health")}
              className="bg-green-600 text-white px-4 py-2 rounded-md ml-2"
            >
              Health
            </button>
            <button
              onClick={() => filterByCategory("Lifestyle")}
              className="bg-yellow-600 text-white px-4 py-2 rounded-md ml-2"
            >
              Lifestyle
            </button>
          </div>

          {blog.category === categoryFilter || categoryFilter === "" ? (
            <>
              {blog.img && (
                <img
                  src={blog.img}
                  alt={`Image for ${blog.title}`}
                  className="w-full h-auto rounded-lg mb-4"
                />
              )}

              <div className="text-gray-700 leading-relaxed mb-6">{blog.body}</div>
              <button
                onClick={handleDelete}
                className="bg-pink-600 hover:bg-pink-700 text-white font-medium px-4 py-2 rounded-md transition duration-200"
              >
                Delete
              </button>
            </>
          ) : null}
        </article>
      )}
    </div>
  );
};

export default BlogDetails;
