import React, { useState, useEffect } from 'react';
import BlogList from './BlogList';
import useFetch from './useFetch';

const Home = () => {
  const { data: blogs, isPending, error } = useFetch('http://localhost:8000/Autorization');

  // Ajout de l'état du formulaire de login
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  // Fonction simple de "connexion" simulée
  const handleLogin = (e) => {
    e.preventDefault();
    if (email === 'admin@example.com' && password === 'admin123') {
      setLoggedIn(true);
    } else {
      alert('Identifiants invalides');
    }
  };

  return (
    <div className="home p-4">
      {/* Partie Login */}
      {!loggedIn && (
        <div className="max-w-md mx-auto bg-white rounded-xl shadow-md p-6 mb-6">
          <h2 className="text-2xl font-bold mb-4 text-center">Se connecter</h2>
          <form onSubmit={handleLogin} className="space-y-4">
            <input
              type="email"
              placeholder="Email"
              className="w-full p-2 border border-gray-300 rounded"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Mot de passe"
              className="w-full p-2 border border-gray-300 rounded"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button
              type="submit"
              className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition"
            >
              Connexion
            </button>
          </form>
        </div>
      )}

      {/* Partie Blogs (affichée si connecté) */}
      {loggedIn && (
        <>
          {error && <div>{error}</div>}
          {isPending && <div>Loading.....</div>}
          {blogs && <BlogList blogs={blogs} title="Tous les Blogs" />}
        </>
      )}
    </div>
  );
};

export default Home;
