import { useState } from "react";
import { useHistory } from "react-router-dom";

const Create = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [author, setAuthor] = useState('mario');
  const [photo, setPhoto] = useState(null); // هنا خاص نغيروها لـ null في البداية
  const [isPending, setIsPending] = useState(false);
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    const blog = { title, body, author, photo }; // هنا ديرنا photo فـ الكائن
    setIsPending(true);

    // upload the photo first if needed, or directly create blog if it's not uploaded
    // ممكن تستعمل fetch لرفع الصورة واحتفظ برابط الصورة فـ blog.

    fetch('http://localhost:8000/blogs', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      console.log('Blog added');
      setIsPending(false);
      history.push('/Home');
    });
  }

  // Handler for file input
  const handleFileChange = (e) => {
    const file = e.target.files[0]; 
    setPhoto(file); // نخزن الصورة في الstate
  }

  return (
    <div className="create">
      <h2>Add a Blog</h2>
      <form onSubmit={handleSubmit}>
        <label>Blog title:</label>
        <input
          type='text'
          required
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Blog body:</label>
        <textarea
          required
          value={body}
          onChange={(e) => setBody(e.target.value)}
        />
        <label>Blog Author:</label>
        <input
          type='text'
          required
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />

        {/* Input for image upload */}
        <label>Upload Image:</label>
        <input
          type="file"
          accept="image/*" 
          onChange={handleFileChange}
        />

        {!isPending && <button>Add Blog</button>}
        {isPending && <button disabled>Adding Blog...</button>}
      </form>
    </div>
  );
}

export default Create;
